import React from 'react';
function CreePost({ input , setInput}){


    const onInputChange =(e)=>{
        setInput(e.target.value);
    };
   const onClickSubmit =(e)=>{
    setInput([...input,{id:input.length+1,id_note: ""}]); 
   }
    return<>
    <form onSubmit={onClickSubmit}>

        <input type="text" className='creeNote' 
        placeholder='Saisissez une note...' 
        onChange={onInputChange}/>
        <div id="afficheNote">
           <img src="../image/send.png" alt="" id="logoAffiche"/>
        </div>
    </form>
    </>;
}
export default CreePost ;
