import React from "react";
import ChoixVote from ""

function PosteVote({ post, choix, setChoix }) {
  return (
    <>
      <div className="vote">
        <h4 className="dateNote">{post.date}</h4>
        <div className="voteshadow">
          <p className="titleVote">cvb</p>

          {choix.map(function (choi) {
        if (choi.id_vote === post.id_vote) {
          return <PosteNote post={post} />;
        }
        return null;
      })}

        </div>
      </div>
    </>
  );
}

export default PosteVote;
